  async updateGradingPage(courseID: string, gradeItemID: string) {
    this.gradingUsers = [];
    const jsonResponse = await this.requestGradingUsers(courseID, gradeItemID);
    const userGrades = JSON.parse(jsonResponse).Objects;
    for (const usergrade of userGrades) {
      this.gradingUsers.push({
        Name: usergrade.User.DisplayName,
        WLUID: usergrade.User.OrgDefinedId,
        WLUEmail: usergrade.User.EmailAddress,
        gradeItemId: usergrade.GradeValue.GradeObjectIdentifier,
        gradeOutOf: usergrade.GradeValue.PointsDenominator,
        currGrade: usergrade.GradeValue.PointsNumerator
      });
    }
    this.gradingUsersFiltered = this.gradingUsers;
    console.log('Grading page updated: ' + this.gradingUsers.length);
    this.gradingUsersFilteredSubject.next('List updated');
  }

  updateSideMenu(jsonResponse) {
    this.sideMenuItems = [];
    const courses = JSON.parse(jsonResponse).Items;
    let cc: string = "";
    
    for (const course of courses) {
      if (course.OrgUnit.Type.Id !== 3) { continue; }
      if(course.OrgUnit.Name.includes("CP")){cc="laptop"}
      else if(course.OrgUnit.Name.includes("EC")){cc="cash"}
      else if(course.OrgUnit.Name.includes("BU")){cc="briefcase"}
      else if(course.OrgUnit.Name.includes("BBA")){cc="briefcase"}
      else if(course.OrgUnit.Name.includes("MA")){cc="calculator"}
      else if(course.OrgUnit.Name.includes("Mathmatics")){cc="calculator"}
      else if(course.OrgUnit.Name.includes("PC")){cc="laptop"}
      else if(course.OrgUnit.Name.includes("Clicker")){cc="keypad"}
      else{cc="apps"}
      this.sideMenuItems.push({
        Name: course.OrgUnit.Name,
        courseID: course.OrgUnit.courseID,
        icon:cc
      });
    }
    this.sideMenuSubject.next('new Menu Items.');
  }

  updateGradeItems(courseID: number) {
    // TODO: Actually Requesting ITEMS instead of fooling yourself. sample response is used
    this.gradeItems = [];
    const jsonResponse = this.gradeItemsCP264;

    this.gradeItems = JSON.parse(jsonResponse);

    this.gradeItemsMenuItems = [];
    this.assignmentItems = [];
    this.labItems = [];
    this.otherItems = [];

    let labCounter = 0;
    let assignmentCounter = 0;
    let otherCounter = 0;

    // push Labs, assignments and other into their correposnding arrays.
    for (const item of this.gradeItems) {
      if (item.Name.charAt(0) === 'L' || item.Name.toLowerCase().includes('lab')) {
        if (labCounter === 0) {
          this.labItems.push({
            Name: 'Lab',
            gradeItemID: '000000',
            allowExceed: false,
            maxGrade: 0
          });
          this.labItems.push({
            Name: item.Name,
            gradeItemID: item.Id,
            allowExceed: Boolean(item.CanExceedMaxPoints),
            maxGrade: item.MaxPoints
          });
        } else {
          this.labItems.push({
            Name: item.Name,
            gradeItemID: item.Id,
            allowExceed: Boolean(item.CanExceedMaxPoints),
            maxGrade: item.MaxPoints
          });
        }
        labCounter += 1;
      } else if (item.Name.charAt(0) === 'A' || item.Name.toLowerCase().includes('assignment')) {
        if (assignmentCounter === 0) {
          this.assignmentItems.push({
            Name: 'Assignment',
            gradeItemID: '000000',
            allowExceed: Boolean(item.CanExceedMaxPoints),
            maxGrade: item.MaxPoints
          });
          this.assignmentItems.push({
            Name: item.Name,
            gradeItemID: item.Id,
            allowExceed: Boolean(item.CanExceedMaxPoints),
            maxGrade: item.MaxPoints
          });
        } else {
          this.assignmentItems.push({
            Name: item.Name,
            gradeItemID: item.Id,
            allowExceed: Boolean(item.CanExceedMaxPoints),
            maxGrade: item.MaxPoints
          });
        }
        assignmentCounter += 1;
      } else {
        if (otherCounter === 0) {
          this.otherItems.push({
            Name: 'Other',
            gradeItemID: '000000',
            allowExceed: Boolean(item.CanExceedMaxPoints),
            maxGrade: item.MaxPoints
          });
          this.otherItems.push({
            Name: item.Name,
            gradeItemID: item.Id,
            allowExceed: Boolean(item.CanExceedMaxPoints),
            maxGrade: item.MaxPoints
          });
        } else {
          this.otherItems.push({
            Name: item.Name,
            gradeItemID: item.Id,
            allowExceed: Boolean(item.CanExceedMaxPoints),
            maxGrade: item.MaxPoints
          });
        }
        otherCounter += 1;
      }
    }

    // sort each array here
    this.labItems.sort((a, b) => {
      if (a.Name > b.Name) {
        return 1;
      }
      if (a.Name < b.Name) {
        return -1;
      }
      return 0;
    });

    this.assignmentItems.sort((a, b) => {
      if (a.Name > b.Name) {
        return 1;
      }
      if (a.Name < b.Name) {
        return -1;
      }
      return 0;
    });

    this.otherItems.sort((a, b) => {
      if (a.Name > b.Name) {
        return 1;
      }
      if (a.Name < b.Name) {
        return -1;
      }
      return 0;
    });

    // merge all the arrays into gradeItemsMenueItems
    Array.prototype.push.apply(this.gradeItemsMenuItems, this.labItems);
    Array.prototype.push.apply(this.gradeItemsMenuItems, this.assignmentItems);
    Array.prototype.push.apply(this.gradeItemsMenuItems, this.otherItems);

    this.gradeItemsMenuSubject.next('New Course has been selected!!');
  }



  filterItems(searchTerm: string) {
    console.log('filtering with term: ' + searchTerm);
    if (searchTerm === '') {
      this.gradingUsersFiltered = this.gradingUsers;

    } else {
      this.gradingUsersFiltered = this.gradingUsers.filter(item => {
        return item.Name.toLowerCase().indexOf(searchTerm.toLowerCase()) > -1 || item.WLUEmail.toLowerCase().indexOf(searchTerm.toLowerCase()) > -1 || item.WLUID.toString().indexOf(searchTerm.toLowerCase()) > -1;
      });
    }
    this.gradingUsersFilteredSubject.next('New filtered items ready.');
  }

