  validateGradeInput(newGrade: string, maxGrade: number, canExceed: boolean): boolean {

    if (newGrade === '' || !isNumeric(newGrade)) { return false; }
    if (!canExceed && Number(newGrade) > maxGrade) { return false; }
    return Number(newGrade) >= 0;
  }


